package day2;

public class OperTest1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1 = 100;
		int num2 = 50;
		System.out.println(num1 + num2);
		System.out.println(num1 - num2);
		System.out.println(num1 * num2);
		System.out.println(num1 / num2);
		System.out.println(num1 % num2);
		System.out.println(num1 > num2);
		System.out.println(num1 < num2);
		System.out.println(num1 == num2);
		System.out.println(num1 != num2);
		System.out.println(num1 + num2);
		System.out.println(num1 > 0 && num2 > 0);
		System.out.println(num1 > 80 && num2 > 80);
		System.out.println(num1 > 0 || num2 > 0);
		System.out.println(num1 > 80 || num2 > 80);

	}

}
